```{.r .cell-code}
library(ggplot2)
data(diamonds)

#histogram:
hist(diamonds$price, main = "Histogram of Diamond Prices", xlab = "Price", col = "lightblue", border = "black")
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-1.png){width=672}
:::

```{.r .cell-code}
#barchart vertical:
counts <- table(diamonds$cut)
barplot(counts, main = "Bar Chart of Diamond Cuts", xlab = "Cut", ylab = "Frequency", col = "lightblue", border = "black")
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-2.png){width=672}
:::

```{.r .cell-code}
#barchart horizontal:
barplot(counts, horiz = TRUE, main = "Horizontal Bar Chart of Diamond Cuts", xlab = "Frequency", ylab = "Cut", col = "lightblue", border = "black")
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-3.png){width=672}
:::

```{.r .cell-code}
#piechart:
pie(counts, main = "Pie Chart of Diamond Cuts", col = rainbow(length(counts)))
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-4.png){width=672}
:::

```{.r .cell-code}
#boxplot
boxplot(price ~ cut, data = diamonds, main = "Boxplot of Diamond Prices by Cut", xlab = "Cut", ylab = "Price", col = "lightblue")
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-5.png){width=672}
:::

```{.r .cell-code}
#scatterplot
plot(diamonds$carat, diamonds$price, main = "Scatterplot of Price vs Carat", xlab = "Carat", ylab = "Price", col = "blue", pch = 16)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-6.png){width=672}
:::

```{.r .cell-code}
#Now using ggplot2:

#histogram:
histogram <- ggplot(diamonds, aes(price)) + geom_histogram(binwidth=500, fill="lightblue") + ggtitle("Histogram of Diamond Prices")
print(histogram)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-7.png){width=672}
:::

```{.r .cell-code}
#barchart vertical:
barvert <- ggplot(diamonds, aes(cut)) + geom_bar(fill="lightblue") + ggtitle("Bar Chart of Diamond Cuts")
print(barvert)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-8.png){width=672}
:::

```{.r .cell-code}
#barchart horizontal:
barhori <- ggplot(diamonds, aes(x=cut)) + geom_bar(fill="lightblue") + coord_flip() + ggtitle("Horizontal Bar Chart of Diamond Cuts")
print(barhori)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-9.png){width=672}
:::

```{.r .cell-code}
#piechart:
piechart <- ggplot(diamonds, aes(x = "", fill = cut)) + geom_bar(width = 1) + coord_polar(theta = "y") + ggtitle("Pie Chart of Diamond Cuts")
print(piechart)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-10.png){width=672}
:::

```{.r .cell-code}
#boxplot:
boxplot <- ggplot(diamonds, aes(x=cut, y=price)) + geom_boxplot(fill="lightblue") + ggtitle("Boxplot of Diamond Prices by Cut")
print(boxplot)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-11.png){width=672}
:::

```{.r .cell-code}
#scatterplot:
scatterplot <- ggplot(diamonds, aes(x=carat, y=price)) + geom_point(alpha=0.5, color="blue") + ggtitle("Scatterplot of Price vs Carat")
print(scatterplot)
```

::: {.cell-output-display}
![](Assignment5_files/figure-html/unnamed-chunk-1-12.png){width=672}
:::

